sub sig_public {
    my ($server, $msg, $nick, $address, $target) = @_;
    #print "no return";
    #return if $nick eq $server->{nick};

    $msg =~ s/[\000-\037]//g;
    my $rmsg = $msg;
    $msg = lc($msg);

    my $nono = 0;
    my $thatword = "";
    foreach (@words) { $nono = 1 and $thatword = $_ if $msg =~ /$_/ }
    return unless $nono;
    Irssi::print("Caught $rmsg");
    $msg =~ s/$thatword/\e[2;43m$thatword\e[00m/g;

    Irssi::signal_continue($server, $msg, $nick, $address, $target);
}

Irssi::signal_add_last('message public', 'sig_public');


